// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Context.sol";
import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";

error Raffle_InsufficientEntryFeeSent();
error Raffle_RaffleNotOpen();
error Raffle_UpkeepNotNeeded();
error Raffle_TransferFailed();

contract Raffle is Context, VRFConsumerBaseV2{

     enum RaffleState {
            Open,
            Calculating
        }

    RaffleState public s_raffleState;
    uint256 public immutable i_entranceFee;
    uint256 public immutable i_interval;
    address payable[] public s_players;
    uint256 public s_lastTimestamp;
    VRFCoordinatorV2Interface public immutable i_vrfCoordinator;
    bytes32 public immutable i_gasLane; // keyhash
    uint64 public immutable i_subscriptionId; 
    uint32 public immutable i_callbackGasLimit;
    address public mostRecentWinner;

    uint16 public constant REQUEST_CONFIRMATIONS = 3;
    uint32 public constant NUM_WORDS = 1;


    event NewRaffleEntry (address indexed player);
    event RequestedRaffleWinner (uint256 indexed requestId);
    event WinnerPicked (address indexed winner);

    constructor (uint256 _entranceFee, uint256 _interval, 
    address vrfCoordinatorV2,
    bytes32 _gasLane,
    uint64 _subscriptionId,
    uint32 _callbackGasLimit
    ) 
    VRFConsumerBaseV2 (vrfCoordinatorV2)
    {

        i_entranceFee = _entranceFee;
        i_interval = _interval;
        i_vrfCoordinator = VRFCoordinatorV2Interface(vrfCoordinatorV2);
        i_gasLane = _gasLane;
        i_subscriptionId = _subscriptionId;
        i_callbackGasLimit = _callbackGasLimit;
    }

    function enterRaffle () external
    payable {
        // require (msg.value >= entranceFee, "Raffle: Funds sent were lesser than entrance fee.");
        // require() is expensive

        //Check if enough funds were sent revert with custom error if not.
        if (msg.value < i_entranceFee) {
            revert Raffle_InsufficientEntryFeeSent();
        }

        if (s_raffleState != RaffleState.Open) {
            revert Raffle_RaffleNotOpen();
        }

        s_players.push(payable(_msgSender()));
        emit NewRaffleEntry(_msgSender());

    }

    // 1. Be true after some time interval
    // 2. The lottery to be open 
    // The contract has ETH
    // Keeper has LINK
    function checkUpkeep (bytes memory /*checkData*/) public view returns (bool upkeepNeeded, bytes memory /*performData*/) 
    {
        bool isOpen = (RaffleState.Open == s_raffleState);
        bool timePassed = (block.timestamp - s_lastTimestamp > i_interval);
        bool hasBalance = (address(this).balance > 0);
        bool hasPlayers = (s_players.length > 0);
        upkeepNeeded = timePassed && isOpen && hasBalance && hasPlayers;
        return (upkeepNeeded, "0x0");
    }

    function performUpkeep (bytes calldata /*performData*/) external {
        (bool upkeepNeeded, ) = checkUpkeep(""); 
        if (!upkeepNeeded) revert Raffle_UpkeepNotNeeded();

        s_raffleState = RaffleState.Calculating;
        uint256 requestId = i_vrfCoordinator.requestRandomWords(i_gasLane, 
        i_subscriptionId, 
        REQUEST_CONFIRMATIONS, 
        i_callbackGasLimit, 
        NUM_WORDS);

        emit RequestedRaffleWinner(requestId);

    }    

    function fulfillRandomWords (
        uint256 /*requestId*/, 
        uint256[] memory randomWords
    ) internal override {
        uint indexOfWinner = randomWords[0] % s_players.length;
        address payable recentWinner = s_players[indexOfWinner];
        s_players = new address payable[](0);
        s_raffleState = RaffleState.Open;
        s_lastTimestamp = block.timestamp;
        (bool success, ) = recentWinner.call{value: address(this).balance}("");
        if (!success) {
            revert Raffle_TransferFailed();
        }
    }

}

